import React from 'react';
import styled, { keyframes, ThemeProvider } from 'styled-components';
import LogoComponent from '../subComponents/logoComponent';
import ParticleComponent from '../subComponents/particleComponent';
import PowerButton from '../subComponents/powerButton';
import SocialIcon from '../subComponents/SocialIcon';
import {darkTheme} from './theme'
import boy from '../assests/image/animeboy.png'


const Box = styled.div`
background-color: ${props => props.theme.body};
width: 100vw;
height:100vh;
position:relative;
display:flex;
justify-content:space-evenly;
align-items:center;
overflow:hidden;
`

const float = keyframes`
0% { transform: translateY(-10px)}
50% {transform: translateY(-15px) translateX(15px)}
100% {transform: translateY(-10px)} 
`

const Boy = styled.div`
position:absolute;
top:10%;
right:10%;
width: 20vw;
animation : ${float} 4s ease infinite;
img{
  width:100%;
  height:auto;
}
`

const Main = styled.div`
border:2px solid ${props => props.theme.text};
color: ${props => props.theme.text};
padding:2rem;
width:50vw;
height:60vh;
z-index:3;
line-height:1.5;


display:flex;
justify-content:center;
align-items:center;
font-size:calc(0.6rem + 1vw);
backdrop-filter:blur(4px);

position:absolute;
left:calc(5rem + 5vw);
top:10rem;

font-family:'Ubuntu Mono',monospace;
`


const About = () => {
  return (
    <ThemeProvider theme={darkTheme}>
      <Box>
        <ParticleComponent theme = 'dark'/>
        <LogoComponent theme='dark'/>
        <SocialIcon theme = 'dark'/>
        <PowerButton theme = 'dark'/>
        <Boy>
          <img  src={boy} alt = "codingBoy"/>
        </Boy>
        <Main>
          I'm front-end developer located in India. I love to create simple yet beautiful websites with great user experience.
          <br/><br/>
          I'm interested in the whole frontend stack Like trying new things and building great projects. I'm an independent freelancer and blogger. I love to write blogs and read books.
          <br/><br/>
          I believe everything is an Art when you put your consciousness in it. You can connect with me via social links.  
        </Main>
    </Box>
    </ThemeProvider>
    
  );
}

export default About;
